(function(){if(!window.sdk){window.sdk={init:function(t){try{var e=document.getElementById("ifr_"+t.playerId);if(e)try{e.src="https://scripts.converteai.net/"+t.accountId+"/"+t.playerId+"/player.html",e.onload=function(){console.log("Player carregado via fallback local")}}catch(t){console.error("Erro ao inicializar iframe:",t)}}catch(t){console.error("Erro no SDK fallback:",t)}},create:function(t){try{if(!t||!t.playerId)return void console.error("PlayerId necessário");var e=t.playerId,o=t.accountId||"",r=document.getElementById(t.selector||"player");if(!r)return void console.error("Elemento de destino não encontrado");r.innerHTML='<iframe id="ifr_'+e+'" allowfullscreen="true" style="width:100%;height:100%;border:none" allow="autoplay; encrypted-media; picture-in-picture" referrerpolicy="origin" sandbox="allow-scripts allow-same-origin allow-forms"></iframe>',this.init({playerId:e,accountId:o,targetElement:r})}catch(t){console.error("Erro ao criar player:",t)}}}};window.addEventListener("message",function(t){t.data&&"smartplayer"===t.data.type&&t.data.id&&console.log("Mensagem do player recebida via fallback:",t.data)})})();